mapper.select:
select m_id, m_pw, m_phone, m_regdate, m_status, m_type, m_name, email_confirm 
from member
where m_id = #{m_id}

mapper.update:
UPDATE member set
email_confirm = #{email_confirm}
where m_id = #{m_id}

UPDATE member set
email_confirm = 'Y'
where m_id = #{m_id}

UPDATE member set
	m_pw = #{m_pw},
	m_phone = #{m_phone}, 
	m_name = #{m_name} 
where m_id = #{m_id}

UPDATE member set
	m_phone = #{m_phone},
	m_name = #{m_name} 
where m_id = #{m_id}

UPDATE member set
	m_pw = #{m_pw}
where m_id = #{m_id}

update member set
m_status = 0
where m_id = #{m_id}

update member set
m_status = 1
where m_id = #{m_id}
