mapper.select:
SELECT NVL(count(1), 0) FROM ( select LIST.*, ROWNUM AS RNUM FROM ( select r.hr_no, m.m_id, r.hr_name, to_char(hr_m_date, 'YYYY-MM-DD') as hr_m_date, r.hr_people, m.m_name, to_char(hr_regdate, 'YYYY-MM-DD') as hr_regdate, r.hr_progress FROM recruit r, member m where r.m_id = m.m_id and hr_status = 1
and m.m_name LIKE '%' || #{keyword} || '%'
) LIST )

SELECT NVL(count(1), 0) FROM ( select LIST.*, ROWNUM AS RNUM FROM ( select r.hr_no, m.m_id, r.hr_name, to_char(hr_m_date, 'YYYY-MM-DD') as hr_m_date, r.hr_people, m.m_name, to_char(hr_regdate, 'YYYY-MM-DD') as hr_regdate, r.hr_progress, hr_status FROM recruit r, member m where r.m_id = m.m_id
and m.m_name LIKE '%' || #{keyword} || '%'
) LIST )
