mapper.select:
select hs_no, m_id, hs_area, hs_date, hs_time, m_name, hs_regdate, hs_progress FROM ( select LIST.*, ROWNUM AS RNUM FROM ( select s.hs_no, m.m_id, s.hs_area, to_char(hs_date, 'YYYY-MM-DD') as hs_date, s.hs_time, m.m_name, to_char(hs_regdate, 'YYYY-MM-DD') as hs_regdate, s.hs_progress FROM support s, member m where s.m_id = m.m_id
and m.m_name LIKE '%' || #{keyword} || '%'
)LIST )
RNUM BETWEEN #{start_row} AND #{end_row}
order by hs_regdate desc

SELECT NVL(count(1), 0) FROM ( select LIST.*, ROWNUM AS RNUM FROM ( select s.hs_no, m.m_id, s.hs_area, to_char(hs_date, 'YYYY-MM-DD') as hs_date, s.hs_time, m.m_name, to_char(hs_regdate, 'YYYY-MM-DD') as hs_regdate, s.hs_progress FROM support s, member m where s.m_id = m.m_id
and m.m_name LIKE '%' || #{keyword} || '%'
) LIST )
